-- This script updates the databases structure before migrating the data from
-- version 2.1 to version 2.2
-- it is intended as a standalone script, however, because of the multiple
-- databases related difficulties, it should be parsed by a PHP script in
-- order to connect to and update the right databases.
-- There is one line per query, allowing the PHP function file() to read
-- all lines separately into an array. The xxMAINxx-type markers are there
-- to tell the PHP script which database we're talking about.
-- By always using the keyword "TABLE" in the queries, we should be able
-- to retrieve and modify the table name from the PHP script if needed, which
-- will allow us to deal with the unique-database-type installations
--

-- This first part is for the main database
-- xxMAINxx
ALTER TABLE catalogue ADD currency VARCHAR( 45 ) NOT NULL COMMENT 'default value 840 iso code for usd dollars';
CREATE TABLE IF NOT EXISTS lp_module (  lp_module_id INT(10) UNSIGNED NOT NULL ,   course_code VARCHAR(40) NOT NULL ,   lp_title TINYTEXT NULL ,   lp_description TINYTEXT NULL ,   PRIMARY KEY (lp_module_id, course_code)   ) ENGINE = MyISAM;
DROP TABLE IF EXISTS ecommerce_items ;
CREATE  TABLE IF NOT EXISTS ecommerce_items (   id MEDIUMINT(8) UNSIGNED NOT NULL AUTO_INCREMENT ,   code VARCHAR(255) NOT NULL ,   cost DECIMAL(10,2) NOT NULL DEFAULT '0.00' ,   item_type TINYINT(2) NOT NULL DEFAULT '0' ,   status TINYINT(2) NOT NULL DEFAULT '0' ,   currency TINYINT(2) NOT NULL DEFAULT '0' ,   date_start DATE NOT NULL DEFAULT '0000-00-00' ,   date_end DATE NOT NULL DEFAULT '0000-00-00' ,   PRIMARY KEY (id) ) ENGINE = MyISAM;
DROP TABLE IF EXISTS lp_module_packs ;
CREATE  TABLE IF NOT EXISTS lp_module_packs (   ecommerce_items_id MEDIUMINT(8) UNSIGNED NOT NULL ,   lp_module_lp_module_id INT(10) UNSIGNED NOT NULL ,   lp_module_course_code VARCHAR(40) NOT NULL ,   PRIMARY KEY (ecommerce_items_id, lp_module_lp_module_id, lp_module_course_code)) ENGINE = MyISAM;
DROP TABLE IF EXISTS ecommerce_user_privileges ;
CREATE  TABLE IF NOT EXISTS  ecommerce_user_privileges ( 	user_id int(10) UNSIGNED NOT NULL, 	ecommerce_items_id mediumint(8) UNSIGNED  NOT NULL, 	role varchar(60) DEFAULT NULL, 	group_id int(11) NOT NULL, 	tutor_id int(11) NOT NULL, 	sort int(11) DEFAULT NULL, 	user_course_cat int(11) DEFAULT 0, PRIMARY KEY (  user_id ,  ecommerce_items_id ) ) ENGINE = MyISAM;  
ALTER TABLE  payment_atos CHANGE  transaction_id  transaction_id VARCHAR( 255 ) NULL DEFAULT NULL;
DROP TABLE IF EXISTS payment_settings;
CREATE TABLE payment_settings ( id int(11) NOT NULL AUTO_INCREMENT,   name varchar(255),   value varchar(255),   position int(11) NOT NULL,   gateway_id int(11) NOT NULL COMMENT '0 none , 1 atos, 2 paypal',   PRIMARY KEY (id) ) ENGINE = MyISAM;
